import React, { Component } from 'react';
import { Card, Table, Button, ButtonGroup } from 'react-bootstrap';
import axios from 'axios';

// Importation de FontAwesome
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrash } from '@fortawesome/free-solid-svg-icons';

export default class VoitureListe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      voitures: []
    };
  }

  componentDidMount() {
    const basicAuth = 'Basic ' + btoa('admin:admin1234');

    axios.get('http://localhost:8090/voitures', {
      headers: {
        'Authorization': basicAuth,
        'Content-Type': 'application/json'
      },
      withCredentials: true
    })
    .then(response => {
      this.setState({ voitures: response.data });
    })
    .catch(error => {
      console.error("Erreur lors de la récupération des données :", error);
    });
  }

  render() {
    const { voitures } = this.state;

    return (
      <Card className="border border-dark bg-dark text-white">
        <Card.Header>Liste Voitures</Card.Header>
        <Card.Body>
          <Table bordered hover striped variant="dark" responsive>
            <thead>
              <tr>
                <th>Marque</th>
                <th>Modèle</th>
                <th>Couleur</th>
                <th>Année</th>
                <th>Prix</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {voitures.length > 0 ? (
                voitures.map((voiture, index) => (
                  <tr key={index}>
                    <td>{voiture.marque}</td>
                    <td>{voiture.modele}</td>
                    <td>{voiture.couleur}</td>
                    <td>{voiture.annee}</td>
                    <td>{voiture.prix}</td>
                    <td>
                      <ButtonGroup>
                        <Button size="sm" variant="outline-primary">
                          <FontAwesomeIcon icon={faEdit} />{' '}Éditer
                        </Button>
                        <Button size="sm" variant="outline-danger">
                          <FontAwesomeIcon icon={faTrash} />{' '}Supprimer
                        </Button>
                      </ButtonGroup>
                    </td>
                  </tr>
                ))
              ) : (
                <tr align="center">
                  <td colSpan="6">Aucune Voiture n’est disponible</td>
                </tr>
              )}
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    );
  }
}
