import React, { Component } from 'react';
import { Card, Table } from 'react-bootstrap';
import axios from 'axios';

// Encodage en base64 pour l'authentification basique
const basicAuth = 'Basic ' + btoa('admin:admin1234');

axios.get('http://localhost:8090/api/voitures', {
  headers: {
    'Authorization': basicAuth
  }
})
.then(response => {
  console.log("Données récupérées :", response.data);
})
.catch(error => {
  console.error("Erreur lors de la récupération des données :", error);
});
export default class VoitureListe extends Component {
  constructor(props) {
    super(props);
    this.state = {
      voitures: []
    };
  }

  componentDidMount() {
    axios.get("http://localhost:8090/voitures")
      .then(response => {
        console.log("Réponse API :", response.data); // Inspecter ici
        this.setState({ voitures: response.data });
      })
      .catch(error => {
        console.error("Erreur lors de la récupération des données :", error);
      });
  }
  
  

  render() {
    return (
      <Card className="border border-dark bg-dark text-white">
        <Card.Header>Liste Voitures</Card.Header>
        <Card.Body>
          <Table bordered hover striped variant="dark" responsive>
            <thead>
              <tr>
                <th>Marque</th>
                <th>Modèle</th>
                <th>Couleur</th>
                <th>Année</th>
                <th>Prix</th>
              </tr>
            </thead>
            <tbody>
              {this.state.voitures.length > 0 ? (
                this.state.voitures.map((voiture, index) => (
                  <tr key={index}>
                    <td>{voiture.marque}</td>
                    <td>{voiture.modele}</td>
                    <td>{voiture.couleur}</td>
                    <td>{voiture.annee}</td>
                    <td>{voiture.prix}</td>
                  </tr>
                ))
              ) : (
                <tr align="center">
                  <td colSpan="5">Aucune Voiture n’est disponible</td>
                </tr>
              )}
              {/* Affichage du nombre de voitures disponibles */}
              <tr align="center">
                <td colSpan="5">
                  {this.state.voitures.length} Voiture(s) disponible(s)
                </td>
              </tr>
            </tbody>
          </Table>
        </Card.Body>
      </Card>
    );
  }
}
