import React, { Component } from 'react';
import { Card, Form, Button, Col, Row } from 'react-bootstrap';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import MyToast from './MyToast';
import { faPlusSquare, faSave, faUndo, faCar, faPalette, faCalendar, faDollarSign, faIdCard } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';

export default class Voiture extends Component {
  constructor(props) {
    super(props);

    // Définir l'état initial
    this.initialState = {
      marque: '',
      modele: '',
      couleur: '',
      immatricule: '',
      prix: '',
      annee: '',
      showToast: false, // Contrôle la visibilité du toast
      toastMessage: '', // Message du toast
      toastVariant: 'success' // Type de toast (success, danger, etc.)
    };

    this.state = this.initialState;
    
    // Liaison des méthodes
    this.voitureChange = this.voitureChange.bind(this);
    this.submitVoiture = this.submitVoiture.bind(this);
    this.resetVoiture = this.resetVoiture.bind(this);
  }

  // Méthode pour réinitialiser l'état du formulaire
  resetVoiture = () => {
    this.setState(() => this.initialState);
  };

  // Méthode pour soumettre les données du formulaire
  submitVoiture = (event) => {
    event.preventDefault();

    const voiture = {
      marque: this.state.marque,
      modele: this.state.modele,
      couleur: this.state.couleur,
      immatricule: this.state.immatricule,
      annee: this.state.annee,
      prix: this.state.prix
    };

    // Authentification basique
    const basicAuth = 'Basic ' + btoa('admin:admin1234');

    // Requête POST avec Axios pour ajouter une voiture
    axios.post('http://localhost:8090/voitures', voiture, {
      headers: {
        'Authorization': basicAuth,
        'Content-Type': 'application/json'
      },
      withCredentials: true // Permet d'envoyer des cookies si nécessaire
    })
    .then(response => {
      if (response.data != null) {
        this.setState({ 
          ...this.initialState, 
          showToast: true, 
          toastMessage: "Voiture ajoutée avec succès !", 
          toastVariant: "success" 
        });
      }
    })
    .catch(error => {
      console.error("Erreur lors de l'enregistrement de la voiture :", error);
      this.setState({ 
        showToast: true, 
        toastMessage: "Erreur lors de l'ajout de la voiture !", 
        toastVariant: "danger" 
      });
    });
  };

  // Méthode pour gérer les changements dans les champs du formulaire
  voitureChange = (event) => {
    this.setState({
      [event.target.name]: event.target.value
    });
  };

  render() {
    const { marque, modele, couleur, immatricule, prix, annee, showToast, toastMessage, toastVariant } = this.state;

    return (
      <div>
        <MyToast show={showToast} message={toastMessage} variant={toastVariant} onClose={() => this.setState({ showToast: false })} />

        <Card className="border border-dark bg-dark text-white">
          <Card.Header>
            <FontAwesomeIcon icon={faPlusSquare} /> Ajouter une Voiture
          </Card.Header>
          <Form onReset={this.resetVoiture} onSubmit={this.submitVoiture} id="VoitureFormId">
            <Card.Body>
              <Row>
                <Form.Group as={Col} controlId="formGridMarque">
                  <Form.Label><FontAwesomeIcon icon={faCar} /> Marque</Form.Label>
                  <Form.Control
                    required
                    name="marque"
                    type="text"
                    value={marque}
                    autoComplete="off"
                    onChange={this.voitureChange}
                    className="bg-dark text-white"
                    placeholder="Entrez Marque Voiture"
                  />
                </Form.Group>

                <Form.Group as={Col} controlId="formGridModele">
                  <Form.Label><FontAwesomeIcon icon={faCar} /> Modèle</Form.Label>
                  <Form.Control
                    required
                    name="modele"
                    type="text"
                    value={modele}
                    autoComplete="off"
                    onChange={this.voitureChange}
                    className="bg-dark text-white"
                    placeholder="Entrez Modèle Voiture"
                  />
                </Form.Group>
              </Row>

              <Row>
                <Form.Group as={Col} controlId="formGridCouleur">
                  <Form.Label><FontAwesomeIcon icon={faPalette} /> Couleur</Form.Label>
                  <Form.Control
                    required
                    name="couleur"
                    type="text"
                    value={couleur}
                    autoComplete="off"
                    onChange={this.voitureChange}
                    className="bg-dark text-white"
                    placeholder="Entrez Couleur Voiture"
                  />
                </Form.Group>

                <Form.Group as={Col} controlId="formGridImmatricule">
                  <Form.Label><FontAwesomeIcon icon={faIdCard} /> Immatriculation</Form.Label>
                  <Form.Control
                    required
                    name="immatricule"
                    type="text"
                    value={immatricule}
                    autoComplete="off"
                    onChange={this.voitureChange}
                    className="bg-dark text-white"
                    placeholder="Entrez Immatriculation"
                  />
                </Form.Group>
              </Row>

              <Row>
                <Form.Group as={Col} controlId="formGridAnnee">
                  <Form.Label><FontAwesomeIcon icon={faCalendar} /> Année</Form.Label>
                  <Form.Control
                    required
                    name="annee"
                    type="number"
                    value={annee}
                    autoComplete="off"
                    onChange={this.voitureChange}
                    className="bg-dark text-white"
                    placeholder="Entrez Année"
                  />
                </Form.Group>

                <Form.Group as={Col} controlId="formGridPrix">
                  <Form.Label><FontAwesomeIcon icon={faDollarSign} /> Prix</Form.Label>
                  <Form.Control
                    required
                    name="prix"
                    type="number"
                    value={prix}
                    autoComplete="off"
                    onChange={this.voitureChange}
                    className="bg-dark text-white"
                    placeholder="Entrez Prix"
                  />
                </Form.Group>
              </Row>
            </Card.Body>

            <Card.Footer style={{ textAlign: "right" }}>
              <Button size="sm" variant="success" type="submit">
                <FontAwesomeIcon icon={faSave} /> Submit
              </Button>{' '}
              <Button size="sm" variant="info" type="reset">
                <FontAwesomeIcon icon={faUndo} /> Reset
              </Button>
            </Card.Footer>
          </Form>
        </Card>
      </div>
    );
  }
}
